import React, { Component } from 'react';
import './style.css';
// import {Checkbox} from 'antd';
import "antd/dist/antd.css";

class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: '',
      list: [],
      one:'',
      complate: 0,
      uncomplate: []
    }
    this.getToDoItem = this.getToDoItem.bind(this);
    this.todoListDelete = this.todoListDelete.bind(this);

  };
  render() {
    return (
      <div id='todo'>
        <div id='container'>
          <label htmlFor='input' className="label">添加内容</label>
          <div>
            <input type='text' id='input' onChange={this.toDoListChange.bind(this)} placeholder="请输入要添加的内容"></input>
            <button type='button' onClick={this.toDoListBtnClick.bind(this)} id='submit'>提交</button>
          </div>
        </div>
        <ul>
          {this.getToDoItem()}
        </ul>
        <div>
          <div><span>总事件：</span>{this.state.list.length}</div>
          <div><span>待办事件：</span>{this.state.uncomplate.length}</div>
          <div><span>完成事件：</span>{this.state.complate}</div>
        </div>
      </div>
    )
  };

  //添加一个待办事项
  getToDoItem() {
    return (
      this.state.list.map((item, index) => {
        return (
          <li key={index} index={index} item={item}><span><input type="checkbox"></input>{index}.{item}</span><span onClick={this.todoListDelete.bind(this,index)} className="close">X</span></li>
        )
      })
    )
  };

  //获取改变后的input内容
  toDoListChange(e) {
    const value = e.target.value;
    this.setState(() => ({
      inputValue: value,
    }))
  }

  //点击提交按钮后，待办事项显示在下面
  toDoListBtnClick() {
    const list = this.state.list;
    const inputValue = this.state.inputValue
    this.setState(() => ({
      list: [...list, inputValue],
      // uncomplate: [...list, inputValue]
    }),() => {
      console.log(this.state.list)
    })
  }

  //删除待办事项
  todoListDelete(indexOne,e) {
    const listOne = this.state.list;

    console.log(e.currentTarget.previousSibling.children[0].checked)
      console.log(0)
      listOne.splice(indexOne,1);
      this.setState({
        list:[...listOne],
        // uncomplate: [...listOne]
      },() => {
        console.log(this.state.list)
      })
      // console.log(document.getElementsByTagName('li')[indexOne].children[0].children[0].checked=false);
    // }



  }
  //计算完成的数量
  // onChange(e){
  //   if(`${e.target.checked}` === 'true'){
  //     this.setState({
  //       complate: this.state.complate+1,
  //     },() => {
  //       // console.log(this.state.complate)
  //     })

  //   }
  //   else{
  //     this.setState({
  //       complate: this.state.complate-1,
  //       // uncomplate: this.state.uncomplate.push()
  //     },() => {
  //       // console.log(this.state.complate)
  //     })
  //   }
  // }
}

export default TodoList;
