import React, { Component } from 'react';
import './style.css';
import TodoItem from './TodoItem';
import {Checkbox} from 'antd';
import "antd/dist/antd.css";

function onChange(e){
  console.log(`checked = ${e.target.checked}`)
}
class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: '',
      list: [],
      one:''
    }
    this.getToDoItem = this.getToDoItem.bind(this);
    this.todoListDelete = this.todoListDelete.bind(this);
  };
  render() {
    return (
      <div id='todo'>
        <div id='container'>
          <label htmlFor='input' className="label">添加内容</label>
          <div>
            <input type='text' id='input' onChange={this.toDoListChange.bind(this)} placeholder="请输入要添加的内容"></input>
            <button type='button' onClick={this.toDoListBtnClick.bind(this)} id='submit'>提交</button>
          </div>
        </div>
        <ul>
          {this.getToDoItem()}
        </ul>
        <div>
          <div><span>待办事件：</span>{this.state.list.length}</div>
        </div>
      </div>
    )
  };

  //添加一个待办事项
  getToDoItem() {
    return (
      this.state.list.map((item, index) => {
        return (
          <li key={index} index={index} item={item}><span><Checkbox onChange={onChange}></Checkbox>{index}.{item}</span><span onClick={this.todoListDelete.bind(this,index)} className="close">X</span></li>
        )
      })
    )
  };

  //获取改变后的input内容
  toDoListChange(e) {
    const value = e.target.value;
    this.setState(() => ({
      inputValue: value,
    }))
  }

  //点击提交按钮后，待办事项显示在下面
  toDoListBtnClick() {
    const list = this.state.list;
    const inputValue = this.state.inputValue
    this.setState(() => ({
      list: [...list, inputValue]
    }))
  }

  //删除待办事项
  todoListDelete(indexOne) {
    const listOne = this.state.list;
    listOne.splice(indexOne,1);
    this.setState({
      list:[...listOne]
    })
    console.log(this.state.list)
  }
  //计算完成的数量
  number(){

  }
}

export default TodoList;
